function saveBlob(blob, fileName) {
    var a = document.createElement('a');
    a.href = window.URL.createObjectURL(blob);
    a.download = fileName;
    a.dispatchEvent(new MouseEvent('click'));
}

var els = document.getElementsByTagName("a");
for (let i = 0, l = els.length; i < l; i++) {
    const el = els[i];
    if (el.href.includes('/api/v1/files/')) {
		const newLink = document.createElement("a");
		newLink.innerHTML = el.innerHTML;
		newLink.style.color = "#0000FF";
		newLink.style.cursor = "pointer";
		newLink.aHref = el.href;
		newLink.onclick = function() {
			const oReq = new XMLHttpRequest();
			console.log('GET', newLink.aHref);
			oReq.open("GET", newLink.aHref);
			oReq.responseType = 'blob';
			oReq.onload = function (e) {
				const blob = e.currentTarget.response;
				const fileName = newLink.aHref.split("?originalName=")[1];

				saveBlob(blob, fileName);
			}
			oReq.send();
		}
		console.log('Replacing')
		el.parentNode.replaceChild(newLink, el);
    }
}

